import React, { useState } from 'react';
import Skeleton from 'react-loading-skeleton';

const api = {
  key: "02db419a7c0f44463ec4a1a566dd93b5",
  base: "https://api.openweathermap.org/data/2.5/"
}
function App() {
  const [query, setQuery] = useState('');
  const [weather, setWeather] = useState({});
  const [loader, setLoader] = useState(false);

  const search = evt => {
    if (evt.key === "Enter") {
      setLoader(true);
      setTimeout(async()=>{
        await fetch(`${api.base}weather?q=${query}&units=metric&APPID=${api.key}`)
        .then(res => res.json())
        .then(result => {
          setLoader(false);
          setWeather(result);
          setQuery('');
          console.log(result);
        }, 5000);
      })
    }
  }

  const dateBuilder = (d) => {
    let months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
    let days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];

    let day = days[d.getDay()];
    let date = d.getDate();
    let month = months[d.getMonth()];
    let year = d.getFullYear();

    return `${day} ${month} ${date}, ${year}`
  }

  const loading = () =>{
    setTimeout(()=>{
      return (
        <div className="fetching-div">
          <Skeleton 
          className="skeleton-one"
          height={30}
          width={500}/>
          <Skeleton
          className="skeleton-two"
          height={20}
          width={500}
          />
          <Skeleton
          className="skeleton-three"
          height={50}
          width={200}
          />
          <Skeleton
          className="skeleton-three"
          height={100}
          width={500}
          />
          <Skeleton
          className="skeleton-three"
          height={80}
          width={500}
          />
        </div>
        )
    },4000)

  }

  var currentDate = new Date();
  var date = currentDate.getFullYear;

  return (
    <div className={(typeof weather.main != "undefined") ? ((weather.main.temp < 16) ? 'app-cold' : 'app-warm') : 'app'}>
      {loader?loading():
      <main>
        <div className="search-box">
          <input
            type="text"
            className="search-bar"
            placeholder="Search..."
            onChange={e => setQuery(e.target.value)}
            value={query}
            onKeyPress={search}
          />
        </div>
        {(typeof weather.main != "undefined") ? (
          <div>
            <div className="location-box">
              <div className="location">{weather.name}, {weather.sys.country}</div>
              <div className="date">{dateBuilder(new Date())}</div>
            </div>
            <div className="weather-box">
              <div className="temp">
                {Math.round(weather.main.temp)}°c
        </div>
              <div className="weather">
                {weather.weather[0].description}
        </div>
        <div className="feelslike">
          Feels Like {Math.round(weather.main.feels_like)}°c
        </div>
            </div>
          </div>
        ) : ('')}
      </main>}
    </div>
  );
}

export default App;
